class Hello{

	def log;
	def context;
	def testRunner;

	// The Constuctor
	def Hello(log, context, testRunner){
		this.log = log;
		this.context = context;
		this.testRunner = testRunner;
	}


	// Nethode
	def sayHello(String name){
		log.info("  Hallo "+name);
	}



}

context.setProperty("Hello", new Hello(log,context,testRunner))

def testStep = testRunner.testCase.getTestStepByName("Hello");  // This call geht, weil die TestCase in die selbe Funktion liegt

testStep.run(testRunner, context);

context.Hello.sayHello("Alain");

// This call geht, weil die TestCase nicht in die selbe Funktion liegt

